import React, { Component } from 'react';
import { gantt } from 'dhtmlx-gantt';
import 'dhtmlx-gantt/codebase/dhtmlxgantt.css';
import $ from "jquery";
import { jQuery } from 'jquery';
import jsonTimestamp from '../../static/config.json';
import jsonAction from '../../static/action.json';
import jsonStateLevelScenario from '../../static/state_level_scenario.json';

export default class Gantt extends Component {
    constructor(props){
        super(props);
    }

    componentDidMount() {
        gantt.config.xml_date = "%Y-%m-%d %H:%i";
        const { tasks } = this.props;
        gantt.init(this.ganttContainer);
        gantt.parse(tasks);
        this.configSetup();

        console.log("adding task sections");

        var fs = require('fs');
        //Here we are loading the action from the action.json file
  var actionLoaded = null;
    var actionLabel = [];
    var priority;

    /*var data = fs.readFileSync("../../static/action.json", "utf8");
    var jsonData = JSON.parse(data);
    actionLoaded = jsonData.actions;*/

    console.log(jsonAction);
    //var jsonAction = JSON.parse(action);
    actionLoaded = jsonAction.actions;
    var keys = Object.keys(actionLoaded);
      jQuery.each(keys, function(i, val) {
        actionLabel.push({"key": i, "label": val})
      });

/*
    $.getJSON("../../static/action.json", function(json){
      actionLoaded = json.actions;
      var keys = Object.keys(actionLoaded);
      jQuery.each(keys, function(i, val) {
        actionLabel.push({"key": i, "label": val})
      });
    });*/

    /*var keys = Object.keys(actionLoaded);
    jQuery.each(keys, function(i, val) {
      actionLabel.push({"key": i, "label": val})
    });*/

    //Here we are loading the actors from the json file, this is where you should add the resources and to process data here also
    var players = null;
    var playersLabel = [];
    //var jsonStateLevelScenario = JSON.parse(state_level_scenario);
    players = jsonStateLevelScenario.players;
    var keys = Object.keys(players);
      var cpt = 0;
      jQuery.each(players, function(i, val) {
        jQuery.each(val.actors, function(y, val2) {
          playersLabel.push({"key": cpt, "label": i + "." + val2.name})
          cpt++;
        })
      });
      for(let r in playersLabel){$('#holder').append($('<option>',{value: r,text: playersLabel[r]["label"]}))}
    /*
    var jsonStateLevelScenario = JSON.parse(state_level_scenario);
    players = jsonStateLevelScenario.players;
    $.getJSON("../../static/state_level_scenario.json", function(json){
      players  = json.players;
      var keys = Object.keys(players);
      var cpt = 0;
      jQuery.each(players, function(i, val) {
        jQuery.each(val.actors, function(y, val2) {
          playersLabel.push({"key": cpt, "label": i + "." + val2.name})
          cpt++;
        })
      });
      for(let r in playersLabel){$('#holder').append($('<option>',{value: r,text: playersLabel[r]["label"]}))}
    });*/

    //And here is the config.json file where all the parameters are, such as timestamp and priority for the moment
    //var priority = null;
    var taskConfigText = null;
    var jsonConfig = null;
    //Json configuration file in order to set all parameters

    //var jsonTimestamp = JSON.parse(timestamp);
    jsonConfig = jsonTimestamp;
    priority = jsonTimestamp.priority;
    taskConfigText = jsonTimestamp.task_text;
    for(let r in priority){$('#priority').append($('<option>',{value: r,text: priority[r]}))}

    /*
    var timestamp = $.getJSON("../../static/config.json", function(json){
      jsonConfig = json;
      priority = json.priority;
      taskConfigText = json.task_text;
      for(let r in priority){$('#priority').append($('<option>',{value: r,text: priority[r]}))}
      return json.timestamp;
    });*/

        var task_sections = [
            { name: "description", height: 50, map_to: "text", type: "textarea" },
            { name: "time", height: 72, type: "time", map_to: "auto" },
            { name: "holder", height: 50, map_to:"holder", type:"select",options:playersLabel},
            { name:"action", height: 50, type:"select", options:actionLabel },
            { name:"priority", height: 50, type:"select", options:priority }
        ];

        gantt.config.lightbox.sections = task_sections;

        gantt.attachEvent("onBeforeLightbox", function(id) {
            var task = gantt.getTask(id);
            gantt.config.lightbox.sections = task_sections;
            console.log(task_sections);
            if(task.$new){
                gantt._dhtmlx_confirm({
                    message:"Create task?",
                    title:"Create task?",
                    callback: function(res){
                        console.log("res= " + res);
                        if(res){
                            //..apply values
                            delete task.$new;
                            gantt.addTask(task);
                        }else{
                            gantt.deleteTask(task.id);
                        }
                    }
                });
                return false;
            }
            return true;
        });
    }

    render() {
        this.setColumns();
        const { zoom } = this.props;
        this.setZoom(zoom);
        return (
            <div
                ref={(input) => { this.ganttContainer = input }}
                style={{ width: '100%', height: '100%' }}
            >
            </div>
        );
    }

    setZoom(value) {
        switch (value) {
            case 'Hours':
                gantt.config.scale_unit = 'day';
                gantt.config.date_scale = '%d %M';

                gantt.config.scale_height = 60;
                gantt.config.min_column_width = 30;
                gantt.config.subscales = [
                    { unit:'hour', step:1, date:'%H' }
                ];
            break;
            case 'Days':
                gantt.config.min_column_width = 70;
                gantt.config.scale_unit = 'week';
                gantt.config.date_scale = '#%W';
                gantt.config.subscales = [
                    { unit: 'day', step: 1, date: '%d %M' }
                ];
                gantt.config.scale_height = 60;
            break;
            case 'Months':
                gantt.config.min_column_width = 70;
                gantt.config.scale_unit = 'month';
                gantt.config.date_scale = '%F';
                gantt.config.scale_height = 60;
                gantt.config.subscales = [
                    { unit:'week', step:1, date:'#%W' }
                ];
            break;
            default:
            break;
        }
    }

    shouldComponentUpdate(nextProps) {
        return this.props.zoom !== nextProps.zoom;
    }

    componentDidUpdate() {
        gantt.render();
    }

    setColumns(){
        console.log(gantt.config.columns)

        gantt.config.columns =  [
        {name:"text",       label:"Task name",  tree:true, width: 150 },
        {name:"start_date", label:"Start time", align:"center", resize: true, width: 120 },
        {name:"end_date",   label:"End date",   align:"center", resize: true, width: 120 },
        //{name:"priority",    height:22, type:"select",   map_to:"priority", options:priority, default_value:"Low"},
        {name:"add",        label:"",           width:44 }
    ];
    }

    configSetup() {
      gantt.config.wide_form = 1;
      gantt.config.work_time = true;
      gantt.config.order_branch = true;
      gantt.config.autoscroll = true;
      gantt.config.auto_scheduling = true;
      gantt.config.autoscroll_speed = 100;
      gantt.config.fit_tasks = true;
      gantt.config.scale_unit = this.props.zoom.scale_unit;
      gantt.config.duration_unit = "hour";
      gantt.config.scale_height = 50;
      gantt.config.min_column_width = 1000;
      gantt.config.date_scale = this.props.zoom.date_scale;
      gantt.config.open_tree_initially = true;
      gantt.config.subscales = this.props.zoom.subscales;
      gantt.templates.timeline_cell_class = function(task, date){
        if(!gantt.isWorkTime({task:task, date: date}))
          return "week_end";
        return "";
      };
      gantt.setWorkTime({
        hours: [9,17]
      })
      gantt.setWorkTime({ day:6, hours:false });
      gantt.setWorkTime({ day:7, hours:false });
      gantt.locale.labels["milestone"] = "Milestone";
      gantt.config.buttons_left = ["dhx_save_btn", "dhx_cancel_btn", "milestone"];
      gantt.config.buttons_right = ["dhx_delete_btn"];
      //addTodayMarker();
    }

    showLightbox = () => {
        this.setState({ showLightbox: true });
    }
}
